var github = {
    load : function(username) {
        $.ajax({
            method: 'GET',
            url: 'https://api.github.com/users/' + username + '/repos',
            dataType: 'json',
            success: function(response) {
                $('#portfolio .portfolio__container').html("");
                $.each(response, function(key, value) {
                    if(response[key].has_pages === true && response[key].name !== username + '.github.io') {
                        var link = $('<a href=""/>');
                            link.attr ('href', 'http://johnweland.github.io/' + response[key].name);
                        var item = $('<div class="portfolio__item"/>');
                        var name =  $('<div class="item__name"/>');
                            link.text(response[key].name);
                            name.append(link);
                        var image = $('<img src="http://placehold.it/100x100"/>');
                        var language = $('<div class="item__language"/>');
                            language.text('Primary language: ' + response[key].language);
                        var description = $('<div class="item__description"/>');
                            description.text('Description: ' + response[key].description);
                        item.append(name);
                        //item.append(image);
                        item.append(language);
                        item.append(description);
                        $('#portfolio .portfolio__container').append(item);
                    }
                }); 
            },
            error: function(response) {
                console.log(response);
            }
        });
    }
}

